import { FavoriteBorderOutlined, SearchOutlined, ShoppingCartOutlined } from "@material-ui/icons"
import styled from "styled-components"

const Container = styled.div`
   //display: flex;

`;

const Title = styled.h1`
    padding: 20px;
    align-items: center;
    justify-content: space-between;
   text-shadow: 2px 2px 4px #000000;  cursor: pointer;
    text-align: center;
    color: #083d20;
    font-style: italic;
    font-size: 40px;

  `;
  const Info = styled.div`

  display: flex; 
    align-items: center;
    justify-content: center;
    transition: all 0.5s ease;
    cursor: pointer; 

  `;

const Icon = styled.div`
margin-top: 8em;
padding: 80px;
font-size: 10px;
width: 100px;
height: 100px;
border-radius: 50%;
background-color: white;
display: flex;
align-items: center;
justify-content: center;
margin: 10px;
transition: all 0.5s ease;


&:hover {
    background-color: #e9f5f5;
    transform: scale(1.1);
}
`;



const Advantages = () => {
  return (
    <Container>

      <Title>ADVANTIGES</Title>

        <Info>
        <Icon>
               <img src = "https://www.svgrepo.com/show/33588/website-design.svg"/> 
            </Icon>
            <Icon>
                <img src = "https://cdn-icons-png.flaticon.com/512/74/74426.png"/>
            </Icon>
            <Icon>
                <FavoriteBorderOutlined/>
            </Icon>
        </Info>



    </Container>
  )
}

export default Advantages
